#include&lt;bits/stdc++.h&gt;#define int long long#define lowbit(x)(x&amp;-x)using namespace std;const int N=1e7;int n,m,a[N],c[N];void modify(int x,int y)&#123;//将x位置的值加y 	while(x&lt;=n)&#123;		c[x]+=y;		x+=lowbit(x);	&#125;&#125; int find(int x)&#123;//求序列前x个位置的和	int ans=0; 	while(x!=0)&#123;		ans+=c[x];		x-=lowbit(x);	&#125;	return ans;&#125;signed main()&#123;cin&gt;&gt;n&gt;&gt;m;	for(int i=1;i&lt;=n;i++)&#123;	cin&gt;&gt;a[i];	modify(i,a[i]);	&#125;	for(int i=1;i&lt;=m;i++)&#123;		int opt,x,y;		cin&gt;&gt;opt&gt;&gt;x&gt;&gt;y;		if(opt==1)&#123;			modify(x,y);		&#125;		else cout&lt;&lt;find(y)-find(x-1)&lt;&lt;endl;	&#125;	return 0;&#125;

