#include&lt;bits/stdc++.h&gt;#define int long longusing namespace std;const int N=1e7;int n,m,a[N];int find(int x)&#123;	if(a[x]==x) return x;	a[x]=find(a[x]);	return a[x];&#125; signed main()&#123;	cin&gt;&gt;n&gt;&gt;m;	for(int i=1;i&lt;=n;i++) a[i]=i;	while(m--)&#123;		int z,b,c;		cin&gt;&gt;z&gt;&gt;b&gt;&gt;c;		int x=find(b),y=find(c);		if(z==1)&#123;			if(x!=y) a[x]=y; 		&#125;		if(z==2)&#123;			if(y==x) cout&lt;&lt;&quot;Y&quot;&lt;&lt;endl;			else cout&lt;&lt;&quot;N&quot;&lt;&lt;endl;		&#125;	&#125;	return 0;&#125;

路径压缩

线段树，单点操作，可用于大规模区间相同标记不同值
const int N=1e6;int n,m,a[N],t[4*N];void build(int cur,int l,int r)//当前建立cur节点，在序列上区间是[l,r]&#123;		if(l==r)&#123;		t[cur]=a[l];	&#125;	else&#123;		int mid=(l+r)&gt;&gt;1;		build(2*cur,l,mid);		build(2*cur+1,mid+1,r);		t[cur]=t[cur*2]+t[cur*2+1];	&#125;&#125;void modify(int cur,int l,int r,int x,int y)	//当前访问到cur，cur在序列上属于[l,r]区间，修改是将x位置加上y&#123;	if(l==r)&#123;		t[cur]+=y; 	&#125; 	else&#123;		int mid=(l+r)&gt;&gt;1;		if(x&gt;mid)		&#123;			modify(2*cur+1,mid+1,r,x,y);		&#125;		else		&#123;			modify(2*cur,l,mid,x,y);		&#125;		t[cur]=t[2*cur]+t[2*cur+1];	&#125;&#125;int find(int cur,int l,int r,int x,int y)&#123;	//当前访问到cur，cur在序列中是[l,r]区间，询问求的是[x,y]区间	if(l==x&amp;&amp;r==y)&#123;		return t[cur];	&#125; 	int mid=(l+r)&gt;&gt;1;	if(y&lt;=mid)&#123;		return find(2*cur,l,mid,x,y);	&#125;	else if(x&gt;mid)&#123;		return find(2*cur+1,mid+1,r,x,y);	&#125;	else&#123;		int a=find(2*cur,l,mid,x,y);		int b=find(2*cur+1,mid+1,r,x,y);		return a+b;	&#125;&#125;

线段树 区间
void print()&#123;	cout&lt;&lt;&quot;sum:&quot;;	for(int i=1;i&lt;=2*n-1;i++) cout&lt;&lt;t[i]&lt;&lt;&quot; &quot;;	cout&lt;&lt;endl&lt;&lt;&quot;add:&quot;;	for(int i=1;i&lt;=2*n-1;i++) cout&lt;&lt;add[i]&lt;&lt;&quot; &quot;;	cout&lt;&lt;endl;&#125;void Add(int cur,int l,int r,int v)&#123;	//给区间 [l,r]所有数加v	add[cur]+=v;	t[cur]+=(r-l+1)*v;	return; &#125;void pushdown(int cur,int l,int r)&#123;	//标记下传	if(add[cur]==0) return;	int mid=l+r&gt;&gt;1;	Add(cur*2,l,mid,add[cur]),Add(cur*2+1,mid+1,r,add[cur]);	add[cur]=0;//请空标记 &#125;void modify(int cur,int l,int r,int x,int y,int v)&#123;	//给区间[x,y]所有数加v	if(l&gt;=x &amp;&amp; r&lt;=y) &#123;		Add(cur,l,r,v);		return;	&#125; 	int mid=l+r&gt;&gt;1;	pushdown(cur,l,r);	if(x&lt;=mid) modify(2*cur,l,mid,x,y,v);	if(mid&lt;y) modify(2*cur+1,mid+1,r,x,y,v);	t[cur]=t[2*cur]+t[2*cur+1];&#125;int query(int cur,int l,int r,int x,int y)&#123;	if(l&gt;=x&amp;&amp;r&lt;=y) return t[cur];	int mid=l+r&gt;&gt;1,res=0;	pushdown(cur,l,r);	if(x&lt;=mid) res+=query(cur*2,l,mid,x,y);	if(y&gt;mid) res+=(cur*2+1,mid+1,r,x,y);	return res;&#125;

平衡树 Treap
const int INF=1e9;int pool,rt;struct node&#123;	int l,r,key,priority,cnt,size;	#define l(x) t[x].l	#define r(x) t[x].r	#define v(x) t[x].key	#define p(x) t[x].priority	#define c(x) t[x].cnt	#define s(x) t[x].size&#125;t[N];inline void update(const int &amp;k)&#123;	s(k)=s(l(k))+s(r(k))+c(k);	return;&#125;inline void Zig(int &amp;k)&#123;//右旋 	int y=l(k);	l(k)=r(y);	r(y)=k;	s(y)=s(k);	update(k);	k=y; &#125;inline void Zag(int &amp;k)&#123;//左旋 	int y=r(k);	r(k)=l(y);	l(y)=k;	s(y)=s(k);	update(k);	k=y;&#125;inline void insert(int &amp;k,const int &amp;key)&#123;	if(!k)&#123;		k=++pool,v(k)=key,p(k)=rand();		c(k)=s(k)=1;l(k)=r(k)=0;		return;	&#125;	else s(k)++;	if(v(k)==key) c(k)++;	else if(key&lt;v(k))&#123;		insert(l(k),key);		if(p(l(k))&lt;p(k)) Zig(k);	&#125;	else&#123;		insert(r(k),key);		if(p(r(k))&lt;p(k)) Zag(k); 	&#125;	return;&#125;inline void Delete(int &amp;k,const int &amp;key)&#123;	if(v(k)==key)&#123;		if(c(k)&gt;1) c(k)--,s(k)--;			else if(!l(k)||!r(k)) k=l(k)+r(k);			else if(p(l(k))&lt;p(r(k))) Zig(k),Delete(k,key);				else Zag(k),Delete(k,key);		return; 	&#125;	s(k)--;	if(key&lt;v(k)) Delete(l(k),key);	else Delete(r(k),key);	return;&#125;inline int querypre(const int &amp;key)&#123;	int x=rt,res=-INF;	while(x)&#123;		if(v(x)&lt;=key) res=v(x),x=r(x);		else x=l(x);	&#125;	return res;&#125;inline int querysuf(const int &amp;key)&#123;	int x=rt,res=INF;	while(x)&#123;		if(v(x)&gt;=key) res=v(x),x=l(x);		else x=r(x);	&#125;	return res;&#125;inline int querykth(int k)&#123;	int x=rt;	while(x)&#123;		if(s(l(x))&lt;k&amp;&amp;s(l(x))+c(x)&gt;=k) return v(x);		if(s(l(x))&gt;=k) x=l(x);		else k-=s(l(x))+c(x),x=r(x);	&#125;	return 0;&#125;inline int rank(const int &amp;key)&#123;	int x=rt,res=0;	while(x)&#123;		if(key==v(x)) return res+s(l(x))+1;		if(key&lt;v(x)) x=l(x);		else res+=s(l(x))+c(x),x=r(x);	&#125;	return res;&#125;

