int head[N],cnt,n,m,s,dis[N];struct edge&#123;int to,value,next;&#125;e[N];void add(int u,int v,int w)&#123;	e[++cnt].to=v;	e[cnt].value=w;	e[cnt].next=head[u];	head[u]=cnt;&#125;struct Heap&#123;int u,d;&#125;; priority_queue&lt;Heap&gt; q;bool operator &lt; (const Heap&amp; now,const Heap&amp; rhs)&#123;	return now.d&gt;rhs.d;&#125;inline void init()&#123;	rep(i,1,n) head[i]=-1;	cin&gt;&gt;n&gt;&gt;m&gt;&gt;s;	rep(i,1,m)&#123;		int u,v,w;		cin&gt;&gt;u&gt;&gt;v&gt;&gt;w;		add(u,v,w);	&#125;&#125;void dijkstra()&#123;	rep(i,1,n) dis[i]=2147483647;	dis[s]=0;	q.push((Heap)&#123;s,dis[s]&#125;);	while(q.size())&#123;		Heap x=q.top();		q.pop();		int u=x.u;		if(x.d&gt;dis[u]) continue;		for(int i=head[u];i&gt;0;i=e[i].next)&#123;			int v=e[i].to;			int w=e[i].value;			if(dis[v]&gt;dis[u]+w)&#123;				dis[v]=dis[u]+w;				q.push((Heap)&#123;v,dis[v]&#125;);			&#125;		&#125;	&#125;&#125;

最小生成树
struct edge&#123;	int v,w,next;&#125;e[N];int head[N],dis[N],cnt,n,m,tot,now=1,ans;bool vis[N];void add(int u,int v,int w)&#123;	e[++cnt].v=v;	e[cnt].w=w;	e[cnt].next=head[u]; 		head[u]=cnt;&#125;void read()&#123;	cin&gt;&gt;n&gt;&gt;m;	int u,v,w;	rep(i,1,m)&#123;		cin&gt;&gt;u&gt;&gt;v&gt;&gt;w;		add(u,v,w);		add(v,u,w);	&#125;&#125;int prim()&#123;	rep(i,2,n) dis[i]=INF;	for(int i=head[1];i;i=e[i].next)&#123;		dis[e[i].v]=min(dis[e[i].v],e[i].w);	&#125;	while(++tot&lt;n)&#123;		int minn=INF;		vis[now]=1;		rep(i,1,n)&#123;			if(!vis[i]&amp;&amp;minn&gt;dis[i])&#123;				minn=dis[i];				now=i;			&#125;		&#125;		ans+=minn;		for(int i=head[now];i;i=e[i].next)&#123;			int v=e[i].v;			if(dis[v]&gt;e[i].w&amp;&amp;!vis[v]) dis[v]=e[i].w;		&#125;	&#125;	return ans;&#125;

倍增LCA
#include&lt;bits/stdc++.h&gt;#define int long longusing namespace std;const int N=5e5+10;int fa[N][22],n,m,s,dep[N],vis[N];vector&lt;int&gt; edge[N];void dfs(int x)&#123;	vis[x]=1;	for(int i=0;i&lt;edge[x].size();i++)&#123;		if(!vis[edge[x][i]])		&#123;			dep[edge[x][i]]=dep[x]+1;			fa[edge[x][i]][0]=x;			dfs(edge[x][i]);		&#125;	&#125;&#125;signed main()&#123;	cin&gt;&gt;n&gt;&gt;m&gt;&gt;s;	for(int i=1;i&lt;n;i++)&#123;		int x,y;		cin&gt;&gt;x&gt;&gt;y;		edge[x].push_back(y);		edge[y].push_back(x);	&#125;	dfs(s);	for(int j=1;j&lt;20;j++) &#123;		for(int i=1;i&lt;=n;i++)			fa[i][j]=fa[fa[i][j-1]][j-1];	&#125;	for(int i=1;i&lt;=m;i++)&#123;		int x,y;		cin&gt;&gt;x&gt;&gt;y;		if(dep[x]&gt;dep[y]) swap(x,y);		int d=dep[y]-dep[x];		for(int i=19;i&gt;=0;i--)			if((d&gt;&gt;i)&amp;1) y=fa[y][i];		int lca=0;		if(x==y)&#123;			lca=x;		&#125;		else&#123;		for(int i=19;i&gt;=0;i--)			if(fa[x][i]!=fa[y][i])&#123;				x=fa[x][i];				y=fa[y][i];			&#125;		lca=fa[x][0];		&#125;		cout&lt;&lt;lca&lt;&lt;endl;	&#125;	return 0;&#125;

