struct edge&#123;	int next,value,to;&#125;e[N];int n, m;int head[N],cnt;int root,dfn[N],low[N],stk[N],top,t,num;vector&lt;int&gt; dcc[N];void add(int u, int v)&#123;	e[++cnt].to=v;	e[cnt].next=head[u];	head[u]=cnt;&#125;void tarjan(int u, int root) &#123;	dfn[u]=low[u]=++ t;	stk[++top]=u;	if(u==root&amp;&amp;!head[u]) &#123;		dcc[++num].push_back(u);		return;	&#125;		for(int i=head[u];i;i=e[i].next) &#123;		int v=e[i].to;		if(!dfn[v]) &#123;			tarjan(v,root);			low[u]=min(low[u],low[v]);			if(low[v]&gt;=dfn[u])&#123;				++num;				int y;				do &#123;					y=stk[top --];					dcc[num].push_back(y);				&#125;while(y!=v);				dcc[num].push_back(u);			&#125;		&#125;		else low[u]=min(low[u],dfn[v]);	&#125;&#125;void work()&#123;	cin&gt;&gt;n&gt;&gt;m;	rep(i,1,m)&#123;		int u,v;		cin&gt;&gt;u&gt;&gt;v;		if(u==v) continue;		add(u,v),add(v,u);	&#125;	rep(i,1,n)&#123;		if(!dfn[i])			tarjan(i,i);	&#125;&#125;
